INSERT INTO `situation` (`id`, `text`, `category`) VALUES
	(1, 'К вам подходят проверить документы, которых у вас с собой нет. Вам угрожают отвозом в участок.', 2),
	(2, 'Вы ответственны за распределение финансов в отделе; в этом месяце задерживается перевод денег на зарплату сотрудникам и одна из ваших коллег подходит с личной просьбой – что ей срочно нужны деньги, и она не может ждать.', 1),
	(3, 'Вы проходите мимо шумной компании подростков, которые начинают задирать вашу подругу.', 2),
	(9, 'Вас просят провести публичное выступление, но у вас есть сильный страх оказаться в неловком положении.', 1),
	(10, 'Вы видите, что мама бьет маленького ребенка.', 2),
	(11, 'Врывается полиция и начинается обыск присутствующих, при этом кто-то подбрасывает вам пакетик наркотиков. Вам предъявляют соответствующее обвинение.', 11),
	(12, 'Вы видите, что ваш партнер безответственно транжирит деньги по мелочам.', 3),
	(16, 'Ваши родители требуют от вас, выбрать именно тот ВУЗ/работу/партнера, которые они считают нужным, но который вам не нравиться.', 7),
	(17, 'Врачебная ошибка, обошлась вам очень дорого.', 8),
	(18, 'У всех касс длинные очереди и кто-то просит уступить место впереди.', 9),
	(19, 'Вы попали в аварию по вине другого водителя, вам нанесён значительный материальный ущерб, а ему нечем вам заплатить.', 4),
